import React, { useState } from 'react';

function Transcript({ text = 'Your Transcription will appear here.' }) {
  const [descriptionPlaceholder, setDescriptionPlaceholder] = useState(text);

  return (
    <div className="questionBox">
      <div className="questionBoxText">
        <textarea
          defaultValue={descriptionPlaceholder}
          onChange={(e) => setDescriptionPlaceholder(e.target.value)}
          name="description"
          className="textareaInput"
          rows="10"
        ></textarea>
      </div>
    </div>
  );
}

export default Transcript;
